// import React from "react";

function Greeting() {
  // We can get date From The Date()
  let time = new Date().getHours();
  let greeting = "";
  let csscolor = {};

  if (time >= 1 && time < 12) {
    greeting = "Good Morning";
    csscolor.color = "rgb(8 38 96)";
  } else if (time >= 12 && time <= 17) {
    greeting = "Good Afternoon";
    csscolor.color = "#f4ff00";
  } else if (time >= 17 && time <= 19) {
    greeting = "Good Evening";
    csscolor.color = "#760015";
  } else {
    greeting = "Good Night";
    csscolor.color = "Black";
  }

  const heading = {
    color: "162680",
    textAlign: "center",
    padding: "45px",
    display: "inline-block",
    fontFamily: "Roboto sans-serif",
    border: "4px solid white",
    borderRadius: "80px",
    backgroundColor: "#3a8b0b",
  };

  const body = {
    backgroundColor: "black",
    width: "100%",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  };

  return (
    <div className="body"  style={body}>

      <h1 style={heading}>
        Hello Sir ,<span style={csscolor}>{greeting}</span>
      </h1>
    </div>
  );
}
// export default Greeting;
