import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

// const time = new Date().toLocaleTimeString();

let time = new Date().getHours();
let greeting = "";
let csscolor={};

if (time >= 1 && time < 12) {
  greeting = "Good Morning";
  csscolor.color= "rgb(21 77 187)";

} else if (time >= 12 && time <19) {
  greeting = "Good Afternoon";
  csscolor.color= "#f4ff00";

} else {
  greeting = "Good Night";
  csscolor.color= "Black";
}

const heading = {
  color: "162680",
  textAlign: "center",
  padding: "16px",
  display: "inline-block",
  fontFamily: "Roboto sans-serif",
  border: "4px solid white",
  borderRadius: "70px",
  backgroundColor: "#3a8b0b",
};

ReactDOM.render(
  <>
    <div className="body">
      <h1 style={heading}>Hello Sir ,  <span style={csscolor}>{greeting}</span></h1>
    </div>
  </>,
  document.getElementById("root")
);
